entry.request:
PREFIX foaf: <http://xmlns.com/foaf/0.1/> #ADD by BE4DBPedia 
 PREFIX dbp: <http://dbpedia.org/resource/>
   PREFIX dbp2: <http://dbpedia.org/ontology/>

   SELECT ?title ?wikipedia
   WHERE {      
	  OPTIONAL{<> foaf:isPrimaryTopicOf ?wikipedia }
	  OPTIONAL{<> rdfs:label ?title filter(lang(?title)='en') }

   }

PREFIX foaf: <http://xmlns.com/foaf/0.1/> #ADD by BE4DBPedia 
PREFIX dbpprop: <http://dbpedia.org/property/> #ADD by BE4DBPedia 
 PREFIX dbp: <http://dbpedia.org/resource/>
   PREFIX dbp2: <http://dbpedia.org/ontology/>

   SELECT ?abstract ?thumbnail ?website ?wikipedia ?title ?location ?employees ?revenue ?netIncome ?foundingDate 
   WHERE {
      <> dbp2:abstract ?abstract . 
	  OPTIONAL{<> dbp2:thumbnail ?thumbnail }	 
	  OPTIONAL{<> dbpprop:website ?website}
	  OPTIONAL{<> foaf:isPrimaryTopicOf ?wikipedia}
	  OPTIONAL{<> rdfs:label ?title filter(lang(?title)='en')}
	  OPTIONAL{<> dbp2:locationCity ?location}
	  OPTIONAL{<> dbp2:numberOfEmployees ?employees}
	  OPTIONAL{<> dbp2:revenue ?revenue}
	  OPTIONAL{<> dbp2:netIncome ?netIncome}
	  OPTIONAL{<> dbp2:foundingDate ?foundingDate}

	  filter(lang(?abstract)='en')
   }
